const boundary = 'foo_bar_baz';

const metadata = {
  name: $input.first().json.output.final_content.title,
  mimeType: 'application/vnd.google-apps.document',
};

// Global wrapper with Inter font and line-height for spacing
const html = `
<html>
  <body>
    <div style="font-family: Inter, sans-serif; line-height: 1.6; font-size: 14px; color: #333; margin-bottom: 20px;">
      ${$input.first().json.data}
    </div>
  </body>
</html>`;

// Build multipart/related body
let body = '';
body += `--${boundary}\r\n`;
body += `Content-Type: application/json; charset=UTF-8\r\n\r\n`;
body += `${JSON.stringify(metadata)}\r\n`;
body += `--${boundary}\r\n`;
body += `Content-Type: text/html\r\n\r\n`;
body += `${html}\r\n`;
body += `--${boundary}--\r\n`;

return [
  {
    json: {
      rawData: body,
      headers: {
        'Content-Type': `multipart/related; boundary=${boundary}`
      }
    }
  }
];

// Combined JSON Parser and Section Splitter for n8n Workflow
// This replaces both the Structured Output Parser AND the Split Out node

// Get input data
const inputData = $input.first().json;
console.log('Input data keys:', Object.keys(inputData));

// Try different possible paths for the AI output
let rawOutput = inputData.output || 
                inputData.text || 
                inputData.content || 
                inputData.result ||
                inputData.response ||
                inputData;

console.log('Raw output type:', typeof rawOutput);

// Function to clean and extract JSON from AI response
function extractAndParseJSON(text) {
  if (typeof text === 'object' && text !== null) {
    return text; // Already parsed
  }

  if (typeof text !== 'string') {
    throw new Error('Input is not a string or object');
  }

  // Remove common AI response formatting
  let cleaned = text
    .replace(/```json\s*/g, '') // Remove ```json
    .replace(/```\s*/g, '')     // Remove closing ```
    .replace(/^[^{]*/, '')      // Remove text before first {
    .replace(/[^}]*$/, '')      // Remove text after last }
    .trim();

  // Find the JSON object bounds more carefully
  let braceCount = 0;
  let jsonStart = -1;
  let jsonEnd = -1;

  for (let i = 0; i < cleaned.length; i++) {
    if (cleaned[i] === '{') {
      if (jsonStart === -1) jsonStart = i;
      braceCount++;
    } else if (cleaned[i] === '}') {
      braceCount--;
      if (braceCount === 0 && jsonStart !== -1) {
        jsonEnd = i;
        break;
      }
    }
  }

  if (jsonStart !== -1 && jsonEnd !== -1) {
    cleaned = cleaned.substring(jsonStart, jsonEnd + 1);
  }

  // Parse JSON with error handling
  try {
    return JSON.parse(cleaned);
  } catch (error) {
    // Try to fix common JSON issues
    cleaned = cleaned
      .replace(/'/g, '"')                    // Replace single quotes
      .replace(/(\w+):/g, '"$1":')          // Quote unquoted keys
      .replace(/,\s*}/g, '}')               // Remove trailing commas
      .replace(/,\s*]/g, ']')               // Remove trailing commas in arrays
      .replace(/\n/g, ' ')                  // Remove newlines
      .replace(/\s+/g, ' ');                // Normalize spaces

    return JSON.parse(cleaned);
  }
}

// Function to validate structure and extract sections
function extractSections(data) {
  // Ensure we have the basic structure
  if (!data || typeof data !== 'object') {
    throw new Error('Invalid data structure received');
  }

  // Look for content_outline in various possible locations
  let contentOutline = data.content_outline || 
                      data.contentOutline || 
                      data.outline ||
                      data.content?.outline;

  if (!contentOutline) {
    console.log('Available keys in data:', Object.keys(data));
    throw new Error('No content_outline found in parsed data');
  }

  // Look for sections
  let sections = contentOutline.sections || 
                contentOutline.section || 
                [];

  if (!Array.isArray(sections)) {
    throw new Error('Sections is not an array');
  }

  if (sections.length === 0) {
    console.warn('No sections found, creating default section');
    sections = [{
      heading: "Default Section",
      purpose: "Default content section",
      key_points: ["Default point"],
      examples_needed: ["Default example"],
      word_count_target: 300,
      transition_note: "Default transition"
    }];
  }

  console.log(`Found ${sections.length} sections to process`);
  return { parsedData: data, sections: sections };
}

try {
  // Step 1: Parse the JSON
  const parsedData = extractAndParseJSON(rawOutput);
  console.log('Successfully parsed JSON');

  // Step 2: Extract and validate sections
  const { parsedData: validatedData, sections } = extractSections(parsedData);
  console.log('Successfully extracted sections');

  // Step 3: Return each section as a separate item (like Split Out would do)
  // BUT also include the full parsed data for downstream nodes that need it
  const results = sections.map((section, index) => ({
    json: {
      // The individual section data
      ...section,
      // Also include the full parsed data for reference
      _fullData: validatedData,
      _sectionIndex: index,
      _totalSections: sections.length
    }
  }));

  console.log(`Returning ${results.length} section items`);
  return results;

} catch (error) {
  console.error('Error processing AI output:', error.message);
  console.error('Raw output sample:', rawOutput ? 
    (typeof rawOutput === 'string' ? rawOutput.substring(0, 300) : JSON.stringify(rawOutput).substring(0, 300)) : 
    'No output received'
  );

  // Return a single fallback section to prevent workflow failure
  return [{
    json: {
      heading: "Error: Processing Failed",
      purpose: "Fallback section due to parsing error",
      key_points: ["AI response could not be processed", "Please check the AI model output format"],
      examples_needed: ["Error handling example"],
      word_count_target: 200,
      transition_note: "Error occurred during processing",
      _fullData: {
        research_insights: {
          key_trends: ["Error in processing"],
          pain_points: ["JSON parsing failed"],
          popular_questions: ["Please check AI output"],
          useful_statistics: ["Processing error occurred"],
          examples_to_use: ["Fallback example"]
        },
        content_outline: {
          title: "Error: Processing Failed",
          hook_strategy: "Please check AI model configuration",
          sections: [{
            heading: "Error Section",
            purpose: "Indicate processing failure",
            key_points: ["Check AI output format"],
            examples_needed: ["Error example"],
            word_count_target: 200,
            transition_note: "Error occurred"
          }],
          conclusion_strategy: "Handle errors gracefully"
        }
      },
      _error: {
        message: error.message,
        timestamp: new Date().toISOString()
      }
    }
  }];
}
