// AI Email Classification Simulator
const email = $input.first().json;
const subject = email.subject.toLowerCase();
const text = email.text.toLowerCase();

// Classification logic
let category = 'GENERAL_INQUIRY';
let confidence = 0.8;
let keywords = [];

if (subject.includes('quotation') || subject.includes('quote') || text.includes('quotation') || text.includes('quote') || text.includes('price')) {
  category = 'QUOTE_REQUEST';
  confidence = 0.95;
  keywords = ['pump', 'centrifugal', 'industrial', 'installation'];
} else if (subject.includes('catalog') || subject.includes('datasheet') || subject.includes('brochure') || text.includes('catalog') || text.includes('price list')) {
  category = 'DATASHEET_REQUEST';
  confidence = 0.92;
  keywords = ['industrial', 'valves', 'catalog', 'price list'];
} else if (subject.includes('support') || subject.includes('issue') || text.includes('problem') || text.includes('error')) {
  category = 'TECHNICAL_SUPPORT';
  confidence = 0.88;
  keywords = ['pump', 'model xyz', 'noise', 'vibration'];
}

// Simulate AI response format
const aiResponse = {
  message: {
    content: `${category}:${confidence}\nKeywords: ${keywords.join(', ')}`
  }
};

return { json: aiResponse };

// Parse AI classification response
const aiResponse = $input.all()[1].json.message.content;
const emailData = $input.all()[0].json;

const lines = aiResponse.split('\n');

let category = 'GENERAL_INQUIRY';
let confidence = 0.5;
let keywords = [];

for (const line of lines) {
  if (line.includes(':')) {
    const parts = line.split(':');
    const key = parts[0].trim();
    const value = parts[1].trim();

    if (['QUOTE_REQUEST', 'DATASHEET_REQUEST', 'GENERAL_INQUIRY', 'TECHNICAL_SUPPORT'].includes(key)) {
      category = key;
      confidence = parseFloat(value) || 0.5;
    } else if (key.toLowerCase() === 'keywords') {
      keywords = value.split(',').map(k => k.trim()).filter(k => k.length > 0);
    }
  }
}

return {
  json: {
    ...emailData,
    classification: {
      category,
      confidence,
      keywords
    }
  }
};

